<?phprequire_once('class/helper.php');$name = $_POST['uname'];$pass = $_POST['upass'];if (empty($name) && empty($pass)) {    echo 0;} else {    $pass = crypt($pass,$hobj->SALT);    $str = $hobj->select("wm_upass,wm_uname", "wm_admin", "wm_uname='$name' AND wm_upass='$pass'");    if ($str == "no") {        //echo "Wrong User Name";        echo 0;    } else {        if ($str[0][0] == $pass) {            session_start();            $_SESSION['name'] = $str[0][1];            echo 1;        } else {            echo 0;        }    }}?>