<?phprequire_once("connect.php");class helper extends connect{    function text($type, $name, $class, $data, $value)    {        echo "<input type='$type' name='$name' id='$name' class='$class' placeholder='$data' value='$value'>";    }    function select($field, $table, $condition)    {        $str = "select $field from $table where $condition";        $result = $this->conn->query($str) or die($this->conn->error);        if (mysqli_num_rows($result) == 0) {            return "no";        } else {            while ($row = $result->fetch_array(MYSQLI_ASSOC)) {                $data[] = $row;            }            return ($data);        }    }    function insert($table, $field, $value)    {        $insert = "insert into $table ($field) values ($value)";        $excuteInsquery = $this->conn->query($insert) or die($this->conn->error);        return $excuteInsquery;    }    function update($table, $field, $value, $condition)    {        $update = "update $table set $field=$value where $condition";        $excuteUpquery = $this->conn->query($update) or die($this->conn->error);        return $excuteUpquery;    }    function delete($table, $condition)    {        $str = "delete from $table where $condition";        $excuteDelquery = $this->conn->query($str) or die($this->conn->error);        return $excuteDelquery;    }    function dropdown($field, $table, $condition, $name, $class)    {        echo "<select name='$name' class='$class' id='$name'>";        $ans = $this->select($field, $table, $condition);        if ($ans == "no") {            echo "<option>No data</option>";        } else {            foreach ($ans as $value) {                echo "<option value='$value[0]'>$value[1]</option>";            }        }        echo "</select>";    }    function cascading_dropdown($field, $table, $condition, $name)    {        echo "<select name='$name' id='$name' style='border-radius:5px; border:1px solid #ACACAC; height:30px; color: #393939;	cursor: pointer;	display: block;	font-family: Arial;	font-size: 12px;	margin: 0px 0px 0px 0px;	padding: 5px;	text-align: left;	width: 198px;'>";        $ans = $this->select($field, $table, $condition);        if ($ans == "no") {            echo "<option style='background-color:#E3E3E3; color:#000; padding:5px' value=''>No data</option>";        } else {            echo "<option style='background-color:#E3E3E3; color:#000; padding:5px' value=''>Please Select</option>";            foreach ($ans as $value) {                echo "<option value='$value[0]' style='background-color:#E3E3E3; color:#000; padding:5px'>$value[1]</option>";            }        }        echo "</select>";    }    function dropdown_selected($field, $table, $condition, $name, $class, $s_field, $s_table, $s_condition)    {        $ans1 = $this->select($s_field, $s_table, $s_condition);        $selected = $ans1[0][0];        //echo $selected;        //print_r($ans1);        $ans = $this->select($field, $table, $condition);        //if($selected==$value[0])        //$selected="selected='$selected'";        echo "<select name='$name' class='$class' id='$name'>";        if ($ans == "no") {            echo "<option>No data</option>";        } else {            foreach ($ans as $value) {                if ($value[0] == $selected) {                    echo "<option value='$value[0]' selected='selected'>$value[1]</option>";                } else {                    echo "<option value='$value[0]'>$value[1]</option>";                }            }        }        echo "</select>";    }}$hobj = new helper();?>